options.css:
.card {
    display: flex;
    align-items: center;
    background-color: #000513; /* Cor de fundo do cartão que combina com o efeito neon */
    border-radius: 20px;
    box-shadow: 0 0 2px rgb(0, 169, 255), 0 0 4px rgb(0, 169, 255), 0 0 8px rgb(0, 169, 255); /* Cor neon */
    color: #ffffff; /* Cor do texto */
    position: relative;
    overflow: hidden;
}

.image-container {
    flex: 0 0 auto;
    margin-right: 15px;
}

.image-container img {
    margin-left: 15px;
    border-radius: 10px;
    height: 50px;
    width: 50px; /* garante proporção fixa */
    object-fit: contain; /* evita distorção */
}
.data-container {
    flex: 1;
    display: flex;
    flex-direction: column;
    align-items: flex-end;
    overflow: hidden;
    padding-right: 10px;
    text-align: center;
}
#total-value {
    font-size: clamp(25px, 1.25vw, 50px); /* Tamanho inicial e final do texto */
    font-weight: bold;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    width: 100%;
}

.label {
    font-size: clamp(10px, 1.25vw, 15px); /* Tamanho inicial e final do subtexto */
    color: #ffffff; /* Cor do subtexto */
    margin-top: 10px;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    width: 100%;
}

options.onInit:
// Sets the text from customProperties
const htmlgraphicsText = htmlNode.getElementById('htmlgraphics-text');

if (htmlgraphicsText) {
  htmlgraphicsText.textContent = customProperties.text;

  // Change the text color based on the theme
  if (theme.isDark) {
    htmlgraphicsText.style.color = 'green';
  } else {
    htmlgraphicsText.style.color = 'red';
  }
}

options.onRender:
const valor_total = htmlNode.getElementById('total-value');

if (valor_total) {
  const field = data.series[0]?.fields[0]; // pega o primeiro mesmo
  if (field && field.values.length > 0) {
    const formattedValue = field.values.get(field.values.length - 1);
    valor_total.innerHTML = `${formattedValue}%`;
  } else {
    valor_total.innerHTML = 'Sem valor';
  }
}

