.titulo-container {
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  min-height: 60px;
  padding: 20px 24px;
  background: #1A1A2A;
  border: 1px solid rgba(34, 197, 94, 0.2);
  border-radius: 12px;
  position: relative;
  overflow: hidden;
}

.titulo-container::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 3px;
  background: linear-gradient(90deg, #22C55E, rgba(34, 197, 94, 0.6));
}

.titulo {
  font-family: 'Inter', 'Poppins', -apple-system, BlinkMacSystemFont, "Segoe UI", Arial, sans-serif;
  text-align: center;
  font-size: 20px;
  font-weight: 600;
  color: #E5E7EB;
  
  padding: 0;
  margin: 0;
  
  background: none;
  border: none;
  border-radius: 0;
  
  letter-spacing: 0.8px;
  line-height: 1.5;
  
  box-shadow: none;
}

.titulo:hover {
  /* Sem efeitos */
}

/* Responsividade */
@media (max-width: 768px) {
  .titulo {
    font-size: 16px;
  }
  
  .titulo-container {
    padding: 16px 20px;
    min-height: 50px;
  }
}

.status-card {
  width: 100%;
  height: 100%;
  min-height: 120px;
  display: flex;
  align-items: center;
  padding: 28px 32px;
  background: #1A1A2A !important;
  border-radius: 16px;
  border: 2px solid transparent;
  gap: 20px;
  position: relative;
  overflow: hidden;
}

.status-card::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 4px;
  background: transparent;
  transition: all 0.3s ease;
}

/* ONLINE - Verde */
.status-card[data-status="Online"],
.status-card[data-status*="Online"],
.status-card.status-Online {
  background: linear-gradient(135deg, #1A1A2A 0%, rgba(34, 197, 94, 0.05) 100%) !important;
  border-color: rgba(34, 197, 94, 0.4) !important;
  box-shadow: 
    0 0 0 1px rgba(34, 197, 94, 0.2) inset,
    0 4px 20px rgba(34, 197, 94, 0.2) !important;
}

.status-card[data-status="Online"]::before,
.status-card[data-status*="Online"]::before,
.status-card.status-Online::before {
  background: linear-gradient(90deg, #22C55E, rgba(34, 197, 94, 0.6)) !important;
}

/* OFFLINE - Vermelho */
.status-card[data-status="Offline"],
.status-card[data-status*="Offline"],
.status-card.status-Offline {
  background: linear-gradient(135deg, #1A1A2A 0%, rgba(239, 68, 68, 0.05) 100%) !important;
  border-color: rgba(239, 68, 68, 0.4) !important;
  box-shadow: 
    0 0 0 1px rgba(239, 68, 68, 0.2) inset,
    0 4px 20px rgba(239, 68, 68, 0.2) !important;
}

.status-card[data-status="Offline"]::before,
.status-card[data-status*="Offline"]::before,
.status-card.status-Offline::before {
  background: linear-gradient(90deg, #EF4444, rgba(239, 68, 68, 0.6)) !important;
}

.icon-wrapper {
  display: flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
  padding: 8px;
  background: rgba(255, 255, 255, 0.03);
  border-radius: 12px;
  border: 1px solid rgba(255, 255, 255, 0.08);
  position: relative;
  min-width: 100px;
  min-height: 100px;
}

.equipment-icon {
  width: 85px !important;
  height: 85px !important;
  border-radius: 8px;
  object-fit: contain;
  filter: drop-shadow(0 2px 8px rgba(0, 0, 0, 0.3));
  position: absolute;
}

/* Esconde offline por padrão, mostra online */
.icon-offline {
  display: none !important;
}

.icon-online {
  display: block !important;
}

/* Quando estiver Online - mostra ícone online, esconde offline */
.status-card[data-status="Online"] .icon-online,
.status-card[data-status*="Online"] .icon-online {
  display: block !important;
}

.status-card[data-status="Online"] .icon-offline,
.status-card[data-status*="Online"] .icon-offline {
  display: none !important;
}

/* Quando estiver Offline - mostra ícone offline, esconde online */
.status-card[data-status="Offline"] .icon-online,
.status-card[data-status*="Offline"] .icon-online {
  display: none !important;
}

.status-card[data-status="Offline"] .icon-offline,
.status-card[data-status*="Offline"] .icon-offline {
  display: block !important;
}

.status-content {
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: flex-end;
  text-align: right;
  gap: 6px;
}

.status-text {
  font-family: 'Inter', 'Poppins', -apple-system, BlinkMacSystemFont, sans-serif !important;
  font-size: 42px !important;
  font-weight: 800 !important;
  line-height: 1 !important;
  letter-spacing: 1px !important;
  text-transform: uppercase !important;
  margin: 0 !important;
  padding: 0 !important;
}

/* FORÇA VERDE para ONLINE */
.status-card[data-status="Online"] .status-text,
.status-card[data-status*="Online"] .status-text,
.status-card.status-Online .status-text,
.status-text.status-Online {
  color: #22C55E !important;
  text-shadow: 0 0 20px rgba(34, 197, 94, 0.4) !important;
}

/* FORÇA VERMELHO para OFFLINE */
.status-card[data-status="Offline"] .status-text,
.status-card[data-status*="Offline"] .status-text,
.status-card.status-Offline .status-text,
.status-text.status-Offline {
  color: #EF4444 !important;
  text-shadow: 0 0 20px rgba(239, 68, 68, 0.4) !important;
}

.status-label {
  font-family: 'Inter', 'Poppins', -apple-system, BlinkMacSystemFont, sans-serif;
  font-size: 11px;
  font-weight: 600;
  color: #6B7280;
  text-transform: uppercase;
  letter-spacing: 1.2px;
  line-height: 1.5;
  margin-top: 4px;
}

/* Responsividade */
@media (max-width: 768px) {
  .equipment-icon {
    width: 70px !important;
    height: 70px !important;
  }
  
  .icon-wrapper {
    min-width: 85px;
    min-height: 85px;
  }
}

/* ===== PADRÃO PARA AMBOS OS PAINÉIS ===== */
