}

/* Status Offline - Vermelho */
.status-card[data-status="Offline"],
.status-card[data-status*="Offline"] {
  border-color: rgba(239, 68, 68, 0.3) !important;
  box-shadow: 0 2px 10px rgba(239, 68, 68, 0.1) !important;
  background: #1A1A2A !important;
}

.status-card[data-status="Offline"]::before,
.status-card[data-status*="Offline"]::before {
  background: linear-gradient(90deg, #EF4444, rgba(239, 68, 68, 0.6));
}

/* ===== ÍCONES (PADRÃO) ===== */
.image-container,
.icon-wrapper {
  display: flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
  padding: 6px;
  background: rgba(255, 255, 255, 0.03) !important;
  border-radius: 10px;
  border: 1px solid rgba(255, 255, 255, 0.08);
}

.image-container img,
.equipment-icon {
  width: 56px;
  height: 56px;
  border-radius: 8px;
  object-fit: contain;
  filter: drop-shadow(0 2px 6px rgba(0, 0, 0, 0.3));
}

/* ===== CONTEÚDO (PADRÃO) ===== */
.data-container {
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  gap: 6px;
}

/* Valores principais */
#total-value,
.status-text {
  font-family: 'Inter', 'Poppins', -apple-system, BlinkMacSystemFont, sans-serif !important;
  font-size: 28px !important;
  font-weight: 700 !important;
  color: #E5E7EB !important;
  line-height: 1.2 !important;
  letter-spacing: 0.5px !important;
  margin: 0 !important;
  padding: 0 !important;
  background: none !important;
}

/* Status Online - Verde no texto */
.status-text.status-Online,
.status-card[data-status="Online"] .status-text,
.status-card[data-status*="Online"] .status-text {
  color: #22C55E !important;
}

/* Status Offline - Vermelho no texto */
.status-text.status-Offline,
.status-card[data-status="Offline"] .status-text,
.status-card[data-status*="Offline"] .status-text {
  color: #EF4444 !important;
}

/* Labels (PADRÃO) */
.label {
  font-family: 'Inter', 'Poppins', -apple-system, BlinkMacSystemFont, sans-serif !important;
  font-size: 11px !important;
  font-weight: 600 !important;
  color: #9CA3AF !important;
  text-transform: uppercase !important;
  letter-spacing: 1px !important;
  line-height: 1.4 !important;
  margin: 0 !important;
  padding: 0 !important;
  background: none !important;
}

/* Responsividade */
@media (max-width: 768px) {
  .card,
  .status-card {
    padding: 16px;
    min-height: 90px;
  }
  
  #total-value,
  .status-text {
    font-size: 22px;
  }
  
  .image-container img,
  .equipment-icon {
    width: 48px;
    height: 48px;
  }
  
  .label {
    font-size: 10px;
  }
}

