/* Reset e Base */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

:root {
    --bg-primary: #0F172A;
    --bg-secondary: #1E293B;
    --bg-card: #1E293B;
    --bg-hover: #334155;
    --text-primary: #F1F5F9;
    --text-secondary: #CBD5E1;
    --text-muted: #94A3B8;
    --border-color: #334155;
    --primary: #3B82F6;
    --primary-hover: #2563EB;
    --secondary: #F59E0B;
    --success: #22C55E;
    --warning: #F59E0B;
    --danger: #EF4444;
    --yellow: #FCD34D;
    --orange: #F97316;
    --purple: #A855F7;
    --pink: #EC4899;
    --teal: #14B8A6;
    --indigo: #6366F1;
    --blue: #3B82F6;
    --green: #22C55E;
}

html, body {
    width: 100%;
    height: 100%;
    overflow: hidden;
    margin: 0;
    padding: 0;
}

body {
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Oxygen', 'Ubuntu', 'Cantarell', sans-serif;
    background-color: var(--bg-primary);
    color: var(--text-primary);
    line-height: 1.6;
    position: relative;
    overflow: hidden;
    margin: 0;
    padding: 0;
}

/* Confetti Animation */
.confetti-container {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    z-index: 1000;
    overflow: hidden;
}

.confetti {
    position: absolute;
    width: 14px;
    height: 14px;
    background: #FFD700;
    top: -10px;
    border-radius: 50%;
    animation: confettiFall linear infinite;
    box-shadow: 0 0 8px currentColor, 0 0 12px currentColor;
}

.confetti:nth-child(odd) {
    border-radius: 0;
    transform: rotate(45deg);
    width: 12px;
    height: 12px;
}

.confetti:nth-child(even) {
    border-radius: 50%;
}

.confetti:nth-child(3n) {
    border-radius: 0;
    transform: rotate(30deg);
    width: 10px;
    height: 16px;
}

.confetti-1 {
    left: 10%;
    background: #FFD700;
    animation-duration: 3s;
    animation-delay: 0s;
}

