}

.confetti::before,
.confetti::after {
    content: '';
    position: absolute;
    width: 10px;
    height: 10px;
    border-radius: 50%;
    box-shadow: 0 0 6px currentColor;
}

.confetti::before {
    left: -18px;
    top: 3px;
    background: #FF1744;
    box-shadow: 0 0 8px #FF1744, 0 0 12px #FF1744;
}

.confetti::after {
    right: -18px;
    top: 3px;
    background: #2196F3;
    box-shadow: 0 0 8px #2196F3, 0 0 12px #2196F3;
}

@keyframes confettiFall {
    0% {
        transform: translateY(0) translateX(0) rotate(0deg);
        opacity: 1;
    }
    50% {
        opacity: 1;
    }
    100% {
        transform: translateY(100vh) translateX(calc(var(--drift, 50px) * var(--direction, 1))) rotate(720deg);
        opacity: 0;
    }
}

.confetti-1 { --drift: 30px; --direction: 1; }
.confetti-2 { --drift: -40px; --direction: -1; }
