/* Widescreen Layout */
.widescreen-layout {
    display: flex;
    width: 100vw;
    height: 100vh;
    overflow: hidden;
    margin: 0;
    padding: 0;
    position: absolute;
    top: 0;
    left: 0;
}

/* Main Content */
.main-content {
    flex: 1;
    display: flex;
    flex-direction: column;
    padding: 0 1rem 1rem 1rem;
    background-color: var(--bg-primary);
    overflow: hidden;
    gap: 0.75rem;
    margin: 0;
}

/* Podium Section */
.podium-section {
    flex-shrink: 0;
    margin: 0;
    margin-bottom: 0.25rem;
}

.podium-container {
    display: flex;
    flex-direction: row;
    align-items: flex-end;
    justify-content: center;
    gap: 1rem;
    animation: fadeInUp 0.8s ease-out;
}

.podium-item {
    position: relative;
    background: var(--bg-card);
    border: 1px solid var(--border-color);
    border-radius: 0.75rem;
    padding: 0.75rem;
    text-align: center;
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
    flex: 1;
    max-width: 220px;
    animation: slideInUp 0.6s ease-out backwards;
    overflow: hidden;
}

.podium-second::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: 
        radial-gradient(circle at 20% 30%, rgba(148, 163, 184, 0.2) 0%, transparent 50%),
        radial-gradient(circle at 80% 70%, rgba(148, 163, 184, 0.15) 0%, transparent 50%);
    opacity: 0.6;
    pointer-events: none;
    z-index: 0;
}

.podium-third::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: 
        radial-gradient(circle at 25% 25%, rgba(249, 115, 22, 0.2) 0%, transparent 50%),
        radial-gradient(circle at 75% 75%, rgba(249, 115, 22, 0.15) 0%, transparent 50%);
    opacity: 0.6;
    pointer-events: none;
    z-index: 0;
}

.podium-item:hover {
    transform: translateY(-8px) scale(1.02);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.4);
    border-color: var(--primary);
}

.podium-second {
    animation-delay: 0.1s;
}

.podium-first {
    animation-delay: 0.2s;
    order: 2;
}

.podium-third {
    animation-delay: 0.3s;
}

.podium-bar {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 3px;
    border-radius: 0.75rem 0.75rem 0 0;
    animation: slideDown 0.5s ease-out 0.4s backwards;
}

.podium-bar-second {
    background: #94A3B8;
}

.podium-bar-first {
    background: var(--yellow);
    height: 4px;
    box-shadow: 0 0 10px rgba(252, 211, 77, 0.5);
}

.podium-bar-third {
    background: var(--orange);
}

.podium-star {
    position: absolute;
    top: 0.75rem;
    right: 0.75rem;
    font-size: 1.25rem;
    animation: pulse 2s infinite, rotate 3s linear infinite;
}

.podium-icon-wrapper {
    width: 50px;
    height: 50px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    margin: 0.75rem auto 0.5rem;
    border: 3px solid;
    animation: scaleIn 0.5s ease-out 0.3s backwards;
    transition: transform 0.3s ease;
    position: relative;
    z-index: 1;
}

.podium-item:hover .podium-icon-wrapper {
    transform: scale(1.1) rotate(5deg);
}

.podium-icon-second {
    background: var(--bg-secondary);
    border-color: #94A3B8;
}

.podium-icon-first {
    width: 65px;
    height: 65px;
    background: rgba(252, 211, 77, 0.2);
    border-color: var(--yellow);
    box-shadow: 0 0 20px rgba(252, 211, 77, 0.3);
}

.podium-icon-third {
    background: rgba(249, 115, 22, 0.2);
    border-color: var(--orange);
}

.podium-icon {
    font-size: 1.5rem;
    font-weight: 700;
}

.podium-label {
    font-size: 0.7rem;
    text-transform: uppercase;
    letter-spacing: 0.1em;
    font-weight: 700;
    color: var(--text-muted);
    margin-bottom: 0.5rem;
    display: block;
    animation: fadeIn 0.5s ease-out 0.5s backwards;
    position: relative;
    z-index: 1;
}

.podium-label-first {
    color: var(--yellow);
}

.podium-name {
    font-size: 0.85rem;
    font-weight: 700;
    color: var(--text-primary);
    margin-bottom: 0.5rem;
    line-height: 1.3;
    min-height: 2rem;
    display: flex;
    align-items: center;
    justify-content: center;
    animation: fadeIn 0.5s ease-out 0.6s backwards;
    position: relative;
    z-index: 1;
}

.podium-name-first {
    font-size: 1rem;
    min-height: 2.2rem;
}

.podium-score {
    font-size: 1.5rem;
    font-weight: 900;
    color: var(--text-primary);
    margin-bottom: 0.75rem;
    animation: numberCount 0.8s ease-out 0.7s backwards;
    position: relative;
    z-index: 1;
}

.podium-score-first {
    font-size: 2rem;
    color: var(--secondary);
    text-shadow: 0 0 20px rgba(245, 158, 11, 0.5);
}

.pts {
    font-size: 0.75rem;
    font-weight: 500;
    color: var(--text-muted);
}

.podium-details {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 0.4rem;
    padding-top: 0.75rem;
    border-top: 1px solid var(--border-color);
    text-align: center;
    animation: fadeIn 0.5s ease-out 0.8s backwards;
    position: relative;
    z-index: 1;
}

.podium-details-first {
    border-top-color: rgba(252, 211, 77, 0.3);
}

.detail-item {
    display: flex;
    flex-direction: column;
}

.detail-item.detail-full {
    grid-column: 1 / -1;
}

.detail-value {
    font-size: 1rem;
    font-weight: 700;
    color: var(--text-primary);
    margin-bottom: 0.25rem;
}

.detail-label {
    font-size: 0.65rem;
    color: var(--text-muted);
}

.podium-first {
    background: linear-gradient(135deg, 
        rgba(252, 211, 77, 0.2) 0%, 
        rgba(252, 211, 77, 0.1) 25%,
        var(--bg-card) 50%,
        rgba(252, 211, 77, 0.1) 75%,
        rgba(252, 211, 77, 0.2) 100%);
    background-size: 200% 200%;
    border: 2px solid var(--yellow);
    transform: translateY(-0.75rem);
    z-index: 10;
    box-shadow: 0 10px 40px rgba(252, 211, 77, 0.3),
                0 0 60px rgba(252, 211, 77, 0.1);
    animation: gradientShift 4s ease infinite, slideInUp 0.6s ease-out backwards;
}

.podium-first::after {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: radial-gradient(circle, rgba(252, 211, 77, 0.2) 0%, transparent 70%);
    animation: rotate 15s linear infinite;
    pointer-events: none;
    z-index: -1;
}

.podium-first::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: 
        radial-gradient(circle at 20% 30%, rgba(252, 211, 77, 0.3) 0%, transparent 40%),
        radial-gradient(circle at 80% 70%, rgba(245, 158, 11, 0.2) 0%, transparent 40%);
    opacity: 0.6;
    pointer-events: none;
    z-index: -1;
}

.podium-second {
    order: 3;
    background: linear-gradient(135deg, 
        rgba(148, 163, 184, 0.1) 0%, 
        var(--bg-card) 50%,
        rgba(148, 163, 184, 0.05) 100%);
    background-size: 200% 200%;
    animation: gradientShift 5s ease infinite, slideInUp 0.6s ease-out backwards;
}

.podium-second::after {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: radial-gradient(circle, rgba(148, 163, 184, 0.15) 0%, transparent 70%);
    animation: rotate 20s linear infinite;
    pointer-events: none;
}

.podium-third {
    order: 1;
    background: linear-gradient(135deg, 
        rgba(249, 115, 22, 0.1) 0%, 
        var(--bg-card) 50%,
        rgba(249, 115, 22, 0.05) 100%);
    background-size: 200% 200%;
    animation: gradientShift 5s ease infinite, slideInUp 0.6s ease-out backwards;
}

.podium-third::after {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: radial-gradient(circle, rgba(249, 115, 22, 0.15) 0%, transparent 70%);
    animation: rotate 20s linear infinite reverse;
    pointer-events: none;
}

/* Honors Section */
.honors-section {
    flex-shrink: 0;
    margin-bottom: 0.25rem;
    animation: fadeIn 0.6s ease-out 1s backwards;
}

.honors-title {
    font-size: 1rem;
    font-weight: 700;
    text-transform: uppercase;
    letter-spacing: 0.05em;
    color: var(--text-primary);
    margin-bottom: 0.5rem;
}

.honors-grid {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 0.75rem;
}

.honor-card {
    background: var(--bg-card);
    border: 1px solid var(--border-color);
    border-radius: 0.5rem;
    padding: 0.75rem;
    display: flex;
    align-items: center;
    justify-content: space-between;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    animation: slideInLeft 0.5s ease-out backwards;
}

.honor-card:nth-child(1) {
    animation-delay: 1.1s;
}

.honor-card:nth-child(2) {
    animation-delay: 1.2s;
}

.honor-card:hover {
    background: var(--bg-hover);
    border-color: var(--primary);
    transform: translateX(8px) scale(1.02);
    box-shadow: 0 10px 30px rgba(59, 130, 246, 0.2);
}

.honor-content {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    flex: 1;
}

.honor-rank {
    width: 35px;
    height: 35px;
    background: var(--bg-secondary);
    border-radius: 0.5rem;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 0.9rem;
    font-weight: 900;
    color: var(--text-muted);
    flex-shrink: 0;
    transition: transform 0.3s ease;
}

.honor-card:hover .honor-rank {
    transform: scale(1.1) rotate(5deg);
}

.honor-info {
    flex: 1;
}

.honor-name {
    font-size: 0.8rem;
    font-weight: 700;
    color: var(--text-primary);
    margin-bottom: 0.25rem;
    line-height: 1.3;
}

.honor-stats {
    font-size: 0.75rem;
    color: var(--text-muted);
}

.honor-score {
    text-align: right;
    flex-shrink: 0;
    margin-left: 0.75rem;
}

.score-value {
    font-size: 1.5rem;
    font-weight: 900;
    color: var(--primary);
    display: block;
    line-height: 1;
    transition: transform 0.3s ease;
}

.honor-card:hover .score-value {
    transform: scale(1.15);
}

.score-label {
    font-size: 0.6rem;
    text-transform: uppercase;
    color: var(--text-muted);
    font-weight: 700;
    margin-top: 0.25rem;
}

/* Ranking Section */
.ranking-section {
    flex: 1;
    display: flex;
    flex-direction: column;
    min-height: 0;
    animation: fadeIn 0.6s ease-out 1.3s backwards;
}

.ranking-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 0.5rem;
    gap: 1rem;
    flex-shrink: 0;
}

.ranking-title-wrapper {
    display: flex;
    align-items: center;
    gap: 0.5rem;
}

.ranking-icon {
    font-size: 1.25rem;
    animation: bounce 2s infinite;
}

.ranking-title {
    font-size: 1.25rem;
    font-weight: 700;
    color: var(--text-primary);
}

.search-wrapper {
    position: relative;
    flex-shrink: 0;
}

.search-icon {
    position: absolute;
    left: 0.75rem;
    top: 50%;
    transform: translateY(-50%);
    font-size: 0.875rem;
    color: var(--text-muted);
    pointer-events: none;
}

.search-input {
    width: 250px;
    padding: 0.5rem 0.75rem 0.5rem 2rem;
    background: var(--bg-secondary);
    border: 1px solid var(--border-color);
    border-radius: 0.5rem;
    color: var(--text-primary);
    font-size: 0.875rem;
    outline: none;
    transition: all 0.3s ease;
}

.search-input:focus {
    border-color: var(--primary);
    box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
    transform: scale(1.02);
}

.search-input::placeholder {
    color: var(--text-muted);
}

.table-container {
    background: var(--bg-card);
    border: 1px solid var(--border-color);
    border-radius: 0.5rem;
    overflow: hidden;
    flex: 1;
    display: flex;
    flex-direction: column;
    min-height: 0;
}

.table-scroll {
    overflow: auto;
    flex: 1;
}

.table-scroll::-webkit-scrollbar {
    width: 6px;
    height: 6px;
}

.table-scroll::-webkit-scrollbar-track {
    background: var(--bg-secondary);
}

.table-scroll::-webkit-scrollbar-thumb {
    background: var(--border-color);
    border-radius: 3px;
}

.table-scroll::-webkit-scrollbar-thumb:hover {
    background: var(--text-muted);
}

.ranking-table {
    width: 100%;
    border-collapse: collapse;
    min-width: 1200px;
}

.table-header {
    background: var(--bg-secondary);
    position: sticky;
    top: 0;
    z-index: 10;
}

.table-header th {
    padding: 0.6rem 1rem;
    text-align: left;
    font-size: 0.65rem;
    font-weight: 700;
    text-transform: uppercase;
    letter-spacing: 0.05em;
    color: var(--text-muted);
    border-bottom: 1px solid var(--border-color);
    white-space: nowrap;
}

.th-center {
    text-align: center;
}

.table-row {
    border-bottom: 1px solid var(--border-color);
    transition: all 0.2s ease;
    animation: fadeInRow 0.4s ease-out backwards;
}

.table-row:nth-child(1) { animation-delay: 1.4s; }
.table-row:nth-child(2) { animation-delay: 1.45s; }
.table-row:nth-child(3) { animation-delay: 1.5s; }
.table-row:nth-child(4) { animation-delay: 1.55s; }
.table-row:nth-child(5) { animation-delay: 1.6s; }
.table-row:nth-child(6) { animation-delay: 1.65s; }
.table-row:nth-child(7) { animation-delay: 1.7s; }
.table-row:nth-child(8) { animation-delay: 1.75s; }
.table-row:nth-child(9) { animation-delay: 1.8s; }
.table-row:nth-child(10) { animation-delay: 1.85s; }
.table-row:nth-child(11) { animation-delay: 1.9s; }
.table-row:nth-child(12) { animation-delay: 1.95s; }

.table-row:hover {
    background-color: var(--bg-hover);
    transform: scale(1.01);
}

.table-row td {
    padding: 0.6rem 1rem;
    font-size: 0.75rem;
    color: var(--text-secondary);
    white-space: nowrap;
}

.td-center {
    text-align: center;
}

.td-rank-1 {
    font-size: 1rem;
    font-weight: 900;
    color: var(--yellow);
    animation: glow 2s ease-in-out infinite;
}

.td-rank-2 {
    font-size: 1rem;
    font-weight: 900;
    color: var(--text-muted);
}

.td-rank-3 {
    font-size: 1rem;
    font-weight: 900;
    color: var(--orange);
}

.td-name {
    font-weight: 700;
    color: var(--text-primary);
    white-space: normal;
    max-width: 200px;
}

.td-score {
    font-weight: 700;
    color: var(--primary);
    font-size: 0.95rem;
}

.td-success {
    color: var(--success);
    font-weight: 600;
}

.badges-list {
    display: flex;
    flex-wrap: wrap;
    gap: 0.4rem;
}

.table-badge {
    padding: 0.2rem 0.6rem;
    border-radius: 0.3rem;
    font-size: 0.6rem;
    font-weight: 600;
    border: 1px solid;
    white-space: nowrap;
    transition: all 0.3s ease;
    animation: badgePop 0.3s ease-out backwards;
}

.table-row:hover .table-badge {
    transform: scale(1.05);
}

.badge-blue {
    background: rgba(59, 130, 246, 0.15);
    color: #60A5FA;
    border-color: rgba(59, 130, 246, 0.3);
}

.badge-purple {
    background: rgba(168, 85, 247, 0.15);
    color: #C084FC;
    border-color: rgba(168, 85, 247, 0.3);
}

.badge-green {
    background: rgba(34, 197, 94, 0.15);
    color: #4ADE80;
    border-color: rgba(34, 197, 94, 0.3);
}

.badge-orange {
    background: rgba(249, 115, 22, 0.15);
    color: #FB923C;
    border-color: rgba(249, 115, 22, 0.3);
}

.badge-pink {
    background: rgba(236, 72, 153, 0.15);
    color: #F472B6;
    border-color: rgba(236, 72, 153, 0.3);
}

.badge-teal {
    background: rgba(20, 184, 166, 0.15);
    color: #5EEAD4;
    border-color: rgba(20, 184, 166, 0.3);
}

.badge-indigo {
    background: rgba(99, 102, 241, 0.15);
    color: #818CF8;
    border-color: rgba(99, 102, 241, 0.3);
}

.badge-gray {
    background: rgba(107, 114, 128, 0.15);
    color: #9CA3AF;
    border-color: rgba(107, 114, 128, 0.3);
}

/* Footer */
.footer {
    flex-shrink: 0;
    padding-top: 0.5rem;
    text-align: right;
    border-top: 1px solid var(--border-color);
    margin-top: 0.25rem;
}

.footer-text {
    font-size: 0.7rem;
    color: var(--text-muted);
}

/* Anima√ß√µes */
@keyframes fadeIn {
    from {
        opacity: 0;
    }
    to {
        opacity: 1;
    }
}

@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

@keyframes slideInUp {
    from {
        opacity: 0;
        transform: translateY(50px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

@keyframes slideInLeft {
    from {
        opacity: 0;
        transform: translateX(-30px);
    }
    to {
        opacity: 1;
        transform: translateX(0);
    }
}

@keyframes slideDown {
    from {
        height: 0;
    }
    to {
        height: 100%;
    }
}

@keyframes scaleIn {
    from {
        opacity: 0;
        transform: scale(0.5);
    }
    to {
        opacity: 1;
        transform: scale(1);
    }
}

@keyframes numberCount {
    from {
        opacity: 0;
        transform: scale(0.5) rotate(-10deg);
    }
    to {
        opacity: 1;
        transform: scale(1) rotate(0deg);
    }
}

@keyframes pulse {
    0%, 100% {
        opacity: 1;
        transform: scale(1);
    }
    50% {
        opacity: 0.7;
        transform: scale(1.1);
    }
}

@keyframes rotate {
    from {
        transform: rotate(0deg);
    }
    to {
        transform: rotate(360deg);
    }
}

@keyframes gradientShift {
    0%, 100% {
        background-position: 0% 50%;
    }
    50% {
        background-position: 100% 50%;
    }
}

@keyframes bounce {
    0%, 100% {
        transform: translateY(0);
    }
    50% {
        transform: translateY(-5px);
    }
}

@keyframes glow {
    0%, 100% {
        text-shadow: 0 0 5px rgba(252, 211, 77, 0.5);
    }
    50% {
        text-shadow: 0 0 15px rgba(252, 211, 77, 0.8), 0 0 25px rgba(252, 211, 77, 0.5);
    }
}

@keyframes fadeInRow {
    from {
        opacity: 0;
        transform: translateX(-20px);
    }
    to {
        opacity: 1;
        transform: translateX(0);
    }
}

@keyframes badgePop {
    from {
        opacity: 0;
        transform: scale(0.8);
    }
    to {
        opacity: 1;
        transform: scale(1);
    }
}

/* Responsividade */
@media (max-width: 1400px) {
    .podium-item {
        max-width: 180px;
        padding: 0.75rem;
    }

    .podium-name {
        font-size: 0.8rem;
    }

    .podium-score {
        font-size: 1.5rem;
    }
}

@media (max-width: 1200px) {
    .main-content {
        padding: 1rem;
    }

    .podium-item {
        max-width: 150px;
    }

    .podium-icon-wrapper {
        width: 50px;
        height: 50px;
    }

    .podium-icon-first {
        width: 60px;
        height: 60px;
    }
}

